#!/usr/bin/env python3# -*- coding: utf-8 -*-"""Created on Sat May  1 19:16:01 2021@author: jean-louquetin"""import sysimport osimport cv2from MontageCreator import MontageCreatorif __name__ == '__main__':        if len(sys.argv) <3:        args = "Arguments are :\n"        imloc = "- Main image location : for instance './photos/test.jpg'\n"        sourcesloc = "- Folder containing images : for instance './photos/test.jpg'\n"        outloc = "- Ouput location/name : for instance 'out.pdf'"        print(args+imloc+sourcesloc+outloc)        exit()          main_image = os.path.join(os.path.abspath(os.path.dirname(__file__)), sys.argv[1])    sources_dir = os.path.join(os.path.abspath(os.path.dirname(__file__)), sys.argv[2])       if len(sys.argv) >= 4:        output_path = os.path.join(os.path.abspath(os.path.dirname(__file__)), sys.argv[3])    else:        output_path =  os.path.join(os.path.abspath(os.path.dirname(__file__)), r'out.png')            #sources_dir = r"/Users/jean-louquetin/Desktop/ComputerVisionProject/PhotoMontage/Sources/All_images"    #main_image = r"/Users/jean-louquetin/Desktop/Photos/photos sup/DSC_0082.JPG"    Editor = MontageCreator(sources_dir, main_image)    image = Editor.createMosaic()    Editor.save_image(image, output_path)    cv2.imshow("Mosaic", image)    cv2.waitKey(0)                